import { join } from 'path';
import expect from 'unexpected';
import { stub } from 'sinon';
import ESDocCoverage, { Command } from '../../src/index';
import esDocConfig from '../../esdoc.json';

/** @test {ESDocCoverage} */
describe('ESDocCoverage', function() {
  /** @test {ESDocCoverage.version} */
  describe('.version', function() {
    it('should return current package version', function() {
      expect(ESDocCoverage.version, 'to match', /[0-9]+\.[0-9]+\.[0-9]+/);
    });
  });

  /** @test {ESDocCoverage.withArgs} */
  describe('.withArgs', function() {
    it('should return a set up instance', function() {
      expect(ESDocCoverage.withArgs([]), 'to be a', ESDocCoverage);
    });
  });

  /** @test {ESDocCoverage#constructor} */
  describe('#constructor', function() {
    it('should work without any arguments', function() {
      expect(() => new ESDocCoverage(), 'not to throw');
    });

    it('should store options passed', function() {
      const opts = { test: 123 };
      expect((new ESDocCoverage(opts)).options, 'to be', opts);
    });

    it('should store command passed', function() {
      const command = Command.CheckCoverage;
      expect((new ESDocCoverage({}, command))._command, 'to be', Command.CheckCoverage);
    });
  });

  /** @test {ESDocCoverage#_getESDocConfig} */
  describe('#_getESDocConfig', function() {
    const instance = new ESDocCoverage({});

    it('should fail with invalid path', function() {
      return expect(instance._getESDocConfig(['does/not/exist.json']),
        'to be rejected with', /Unable to find ESDoc config/);
    });

    it('should use esdoc property on package.json files', function() {
      return expect(instance._getESDocConfig(['package.json']),
        'to be rejected with', /Unable to find ESDoc config/);
    });

    it('should be fulfilled if config exists', function() {
      return expect(instance._getESDocConfig(['esdoc.json']),
        'when fulfilled', 'to have properties', {
          config: esDocConfig,
          path: join(__dirname, '../../esdoc.json'),
        });
    });
  });

  /** @test {ESDocCoverage#_getCoverageReport} */
  describe('#_getCoverageReport', function() {
    const instance = new ESDocCoverage({});

    it('should fail with invalid path', function() {
      return expect(instance._getCoverageReport({
        path: 'does/not/exist.json',
        config: { destination: 'docs/api' },
      }),
        'to be rejected with', /Unable to find coverage report/);
    });

    it('should work with valid path', function() {
      return expect(instance._getCoverageReport({
        path: join(__dirname, '../../esdoc.json'),
        config: { destination: 'docs/api' } }),
        'to be fulfilled');
    });
  });

  /** @test {ESDocCoverage#_checkCoverageReport} */
  describe('#_checkCoverageReport', function() {
    it('should return xml report if reporter is set to unit', function() {
      const instance = new ESDocCoverage({ reporter: 'junit' });

      return expect(instance._checkCoverageReport({ files: {
        'test.js': { actualCount: 0, expectCount: 2, undocumentLines: [1, 2] },
      } }), 'to be fulfilled with', /^<\?xml/);
    });
  });

  /** @test {ESDocCoverage#checkCoverage} */
  describe('#checkCoverage', function() {
    it('should call getConfig with passed option value', function() {
      const instance = new ESDocCoverage({ config: 'test.json' });
      stub(instance, '_getESDocConfig').callsFake(() => Promise.resolve());
      stub(instance, '_getCoverageReport').callsFake(() => Promise.resolve());
      stub(instance, '_checkCoverageReport').callsFake(() => Promise.resolve());

      instance.checkCoverage();

      expect(instance._getESDocConfig.calledOnce, 'to be true');
      expect(instance._getESDocConfig.lastCall.args[0], 'to equal', ['test.json']);
    });

    it('should call getConfig with default config paths if no option passed', function() {
      const instance = new ESDocCoverage({});
      stub(instance, '_getESDocConfig').callsFake(() => Promise.resolve());
      stub(instance, '_getCoverageReport').callsFake(() => Promise.resolve());
      stub(instance, '_checkCoverageReport').callsFake(() => Promise.resolve());

      instance.checkCoverage();

      expect(instance._getESDocConfig.calledOnce, 'to be true');
      expect(instance._getESDocConfig.lastCall.args[0], 'to equal', [
        '.esdoc.json',
        '.esdoc.js',
        'package.json',
      ]);
    });
  });

  /** @test {ESDocCoverage#run} */
  describe('#run', function() {
    it('should fail without command', function() {
      return expect((new ESDocCoverage()).run(), 'to be rejected with', /No command/);
    });

    it('should fail with invalid command', function() {
      return expect((new ESDocCoverage({}, 'unknown')).run(),
        'to be rejected with', /Invalid command/);
    });

    it('should always run passed command if defined', function() {
      return expect((new ESDocCoverage({}, Command.CheckCoverage)).run('unknown'),
        'to be rejected with', /Invalid command/);
    });

    it('should call checkCoverage for CheckCoverage command', function() {
      const instance = new ESDocCoverage({}, Command.CheckCoverage);
      stub(instance, 'checkCoverage');

      instance.run();

      expect(instance.checkCoverage.calledOnce, 'to be true');
    });
  });
});

import expect from 'unexpected';
import { stub } from 'sinon';
import CliReporter from '../../../src/lib/CliReporter';

/** @test {CliReporter} */
describe('CliReporter', function() {
  /** @test {CliReporter#constructor} */
  describe('#constructor', function() {
    it('should initialize a cli-table Table', function() {
      expect((new CliReporter()).table, 'to be defined');
    });
  });

  /** @test {CliReporter#addRow} */
  describe('#addRow', function() {
    it('should call cli-table~Table#push', function() {
      const reporter = new CliReporter();
      stub(reporter.table, 'push').callsFake(() => {});

      reporter.addRow('name', false, 0, 2, [1, 2]);
      expect(reporter.table.push.calledOnce, 'to be true');
    });
  });

  /** @test {CliReporter#report} */
  describe('#report', function() {
    it('should forward args to addRow', function() {
      const reporter = new CliReporter();
      stub(reporter, 'addRow').callsFake(() => {});
      const args = ['name', false, 0, 2, [1, 2]];

      reporter.report(...args);
      expect(reporter.addRow.calledOnce, 'to be true');
      expect(reporter.addRow.lastCall.args, 'to equal', args);
    });
  });

  /** @test {CliReporter#finish} */
  describe('#finish', function() {
    it('should add a summary row', function() {
      const reporter = new CliReporter();
      stub(reporter, 'addRow').callsFake(() => {});

      reporter.finish();
      expect(reporter.addRow.calledOnce, 'to be true');
    });

    it('should set process exit code', function() {
      const reporter = new CliReporter();
      reporter.report('name', false, 0, 2, [1, 2]);

      reporter.finish();
      expect(process.exitCode, 'to equal', 1);
    });
  });
});

import expect from 'unexpected';
import JUnitReporter from '../../../src/lib/JUnitReporter';

/** @test {JUnitReporter} */
describe('JUnitReporter', function() {
  /** @test {JUnitReporter#constructor} */
  describe('#constructor', function() {
    it('should initialize results', function() {
      expect((new JUnitReporter()).results, 'to equal', []);
    });
  });

  /** @test {JUnitReporter#report} */
  describe('#report', function() {
    it('should add a testcase', function() {
      const reporter = new JUnitReporter();

      reporter.report('test.js', true, 0, 1, [0, 1]);
      expect(reporter.results, 'to have length', 1);
      expect(reporter.results[0], 'to have own properties', {
        $: {
          classname: 'test.js',
          name: 'should be documented',
        },
      });
    });

    it('should add failure if threshold is not met', function() {
      const reporter = new JUnitReporter();

      reporter.report('test.js', false, 0, 1, [0, 1]);
      expect(reporter.results, 'to have length', 1);
      expect(reporter.results[0], 'to have own properties', {
        failure: [{
          $: { message: 'Missing API documentation' },
          _: 'Lines not documented: 0, 1',
        }],
      });
    });
  });

  /** @test {JUnitReporter#finish} */
  describe('#finish', function() {
    it('should resolve with a XML string', function() {
      expect((new JUnitReporter()).finish(), 'when fulfilled', 'to match', /^<\?xml/)
    });
  });
});

import expect from 'unexpected';
import Reporter from '../../../src/lib/Reporter';

/** @test {Reporter} */
describe('Reporter', function() {
  /** @test {Reporter#constructor} */
  describe('#constructor', function() {
    it('should set initial values', function() {
      expect((new Reporter()), 'to have own properties', {
        actual: 0,
        expected: 0,
        success: true,
        numberOfFiles: 0,
        notCoveredFiles: 0,
      });
    });
  });

  /** @test {Reporter#report} */
  describe('#report', function() {
    it('should increase values', function() {
      const reporter = new Reporter();
      reporter.report('test.js', false, 2, 4);

      expect(reporter, 'to have own properties', {
        numberOfFiles: 1,
        actual: 2,
        expected: 4,
        success: false,
        notCoveredFiles: 1,
      });
    });

    it('should should keep previous success value if successful', function() {
      const reporter = new Reporter();
      reporter.report('test.js', true, 2, 4);

      expect(reporter, 'to have own properties', {
        numberOfFiles: 1,
        actual: 2,
        expected: 4,
        success: true,
        notCoveredFiles: 0,
      });
    });
  });

  /** @test {Reporter#finish} */
  describe('#finish', function() {
    it('should return a fulfilled Promise by default', function() {
      expect((new Reporter()).finish(), 'to be fulfilled');
    });
  });
});
